export const getPlatformsByCategory = (category: string) => {
  if (category === 'All') return aiPlatforms;
  return aiPlatforms.filter(platform => platform.category === category);
};

export const getPlatformOptions = () => {
  return aiPlatforms.map(platform => ({
    value: platform.id,
    label: platform.name,
    category: platform.category,
    color: platform.color,
    logo: platform.logo
  }));
};
